(4193,	386,	'share',	'<ul>\r\n 	<li><a href=\"javascript:void(0)\"><i class=\"fas fa-envelope\"></i></a></li>\r\n        <li><a href=\"javascript:void(0)\"><i class=\"fab fa-facebook-square\"></i></a></li>\r\n        <li><a href=\"javascript:void(0)\"><i class=\"fab fa-twitter\"></i></a></li>\r\n        <li><a href=\"javascript:void(0)\"><i class=\"fab fa-pinterest-square\"></i></a></li>\r\n</ul>'),
